import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [file, setFile] = useState(null);
  const [transcription, setTranscription] = useState('');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
    setError('');
    setTranscription('');
  };

  const handleUpload = async () => {
    if (!file) {
      setError('Please select a file first.');
      return;
    }

    const formData = new FormData();
    formData.append('audio', file);

    setLoading(true);
    setError('');
    setTranscription('');

    try {
      const response = await axios.post('http://localhost:5000/api/transcribe', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      setTranscription(response.data.text);
    } catch (err) {
      console.error(err);
      setError('An error occurred during transcription. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleDownload = () => {
    const element = document.createElement("a");
    const file = new Blob([transcription], {type: 'text/plain'});
    element.href = URL.createObjectURL(file);
    element.download = "transcript.txt";
    document.body.appendChild(element); // Required for this to work in FireFox
    element.click();
  };

  return (
    <div className="app-container">
      <div className="card">
        <h1>Speech to Text Converter</h1>
        
        <div className="upload-section">
          <input type="file" accept="audio/*" onChange={handleFileChange} className="file-input" />
          <button onClick={handleUpload} disabled={loading || !file} className="primary-btn">
            {loading ? 'Processing...' : 'Transcribe Audio'}
          </button>
        </div>

        {loading && (
          <div className="loading-spinner">
            <div className="spinner"></div>
            <p>Transcribing... This may take a moment.</p>
          </div>
        )}

        {error && <div className="error-message">{error}</div>}

        {transcription && (
          <div className="result-section">
            <h2>Transcription Result:</h2>
            <textarea 
              value={transcription} 
              readOnly 
              className="transcription-text"
              rows={10}
            />
            <button onClick={handleDownload} className="secondary-btn">
              Download as TXT
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
