import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { ThemeProvider, useTheme } from './context/ThemeContext';
import Sidebar from './components/Sidebar';
import UploadView from './components/UploadView';
import './App.css';
import { Sun, Moon } from 'lucide-react';

// Placeholder components for other routes
const Placeholder = ({ title }) => (
  <div className="card">
    <h1>{title}</h1>
    <p style={{ color: 'var(--text-secondary)' }}>This feature is coming soon!</p>
  </div>
);

const Layout = ({ children }) => {
  const { theme, toggleTheme } = useTheme();
  
  return (
    <div className="app-layout">
      <Sidebar />
      <main className="main-content">
        {/* Mobile Theme Toggle (Floating) */}
        <button 
          className="mobile-theme-toggle" 
          onClick={toggleTheme}
          style={{ display: window.innerWidth <= 768 ? 'flex' : 'none' }}
        >
          {theme === 'light' ? <Moon size={20} /> : <Sun size={20} />}
        </button>
        {children}
      </main>
    </div>
  );
};

function App() {
  return (
    <ThemeProvider>
      <Router>
        <Layout>
          <Routes>
            <Route path="/" element={<Navigate to="/upload" replace />} />
            <Route path="/upload" element={<UploadView />} />
            <Route path="/record" element={<Placeholder title="Record Audio" />} />
            <Route path="/history" element={<Placeholder title="Transcription History" />} />
            <Route path="/settings" element={<Placeholder title="Settings" />} />
          </Routes>
        </Layout>
      </Router>
    </ThemeProvider>
  );
}

export default App;
